INSERT INTO `hl_article` VALUES (1270, '提供能够被技术和非技术用户广泛的理解和共享的业务术语表', NULL, NULL, NULL, '可以实现快速查找，深度理解，更具平台信任感和数据共享安全性，可跨越整个数据产业。是适用于高遵从性和高业务敏捷性的数据智能平台。', NULL, NULL, NULL, 'uploads/article/20180906/5b9148d1040cb3.45570285.png', NULL, '2018-08-02 20:45:11', '2018-09-06 23:33:37', 0, 414, 0, '华矩', '2018-08-02 20:43:46', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0.00, NULL, NULL, NULL, NULL, NULL, 50, NULL, 0);
INSERT INTO `hl_article` VALUES (1271, '数据生命周期管理', NULL, NULL, NULL, '跟踪管理在信息系统的数据在整个生命周期内的流动：从创建和初始存储，到它过时被删除。', NULL, NULL, NULL, 'uploads/article/20180906/5b9148d97e5833.75072184.png', NULL, '2018-08-02 20:45:11', '2018-09-06 23:33:45', 0, 414, 0, '华矩', '2018-08-02 20:43:46', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0.00, NULL, NULL, NULL, NULL, NULL, 50, NULL, 0);
INSERT INTO `hl_article` VALUES (1272, '高效性', NULL, NULL, NULL, '面对快速更迭的数据资产，爆炸式的数据量，更新高速，瞬息万变，可以在最短的时间内定位数据源问题，找到数据变化更新路径，跟踪分类数据。', NULL, NULL, NULL, 'uploads/article/20180906/5b9148e3a53234.07857995.png', NULL, '2018-08-02 20:45:11', '2018-09-06 23:33:55', 0, 414, 0, '华矩', '2018-08-02 20:43:46', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0.00, NULL, NULL, NULL, NULL, NULL, 50, NULL, 0);
