INSERT INTO `hl_article` VALUES (1429, '决策支持', NULL, NULL, NULL, '通过快速、精准、智能的数据匹配和挖掘，在第一时间进行消费者洞察、精准匹配目标受众、智能选择投放渠道，为企业商业决策提供数据支撑。', NULL, NULL, NULL, 'uploads/article/20180906/5b9115c8a2f352.41044151.png', NULL, '2018-09-06 19:17:29', '2018-09-06 19:55:52', 0, 500, 0, NULL, '2018-09-06 19:17:13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
INSERT INTO `hl_article` VALUES (1430, '数据质量优化', NULL, NULL, NULL, '实现数据清洗、去重、整理，使得数据更加完整和准确，建立业务规则使之自动服务于数据质量管理流程，确保用户数据的准确性。', NULL, NULL, NULL, 'uploads/article/20180906/5b9116035b9c54.71693183.png', NULL, '2018-09-06 19:18:16', '2018-09-06 19:56:51', 0, 501, 0, NULL, '2018-09-06 19:17:57', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
INSERT INTO `hl_article` VALUES (1431, '数据价值挖掘', NULL, NULL, NULL, '按照清洗整理好的数据进行用户维度分析获取直接维度与间接维度。根据不同的维度，形成用户画像，辅助运营投放团队进行维度选择，得到所需用户多维度数据，为后续精准投放决策提供数据基础。', NULL, NULL, NULL, 'uploads/article/20180906/5b91160d1a8fd7.04675782.png', NULL, '2018-09-06 19:18:57', '2018-09-06 19:57:01', 0, 501, 0, NULL, '2018-09-06 19:18:24', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
INSERT INTO `hl_article` VALUES (1432, '提升营销精准度', NULL, NULL, NULL, '多维度的用户画像，使得客户在对用户进行产品推送或广告投放时能更符合用户需求，在提高用户体验的同时，也提高了营销转化率。', NULL, NULL, NULL, 'uploads/article/20180906/5b91161ccb0725.83392994.png', NULL, '2018-09-06 19:19:40', '2018-09-06 19:57:16', 0, 501, 0, NULL, '2018-09-06 19:19:10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
INSERT INTO `hl_article` VALUES (1434, '华矩科技助力互联网电商企业建立用户画像以实现精准营销', NULL, NULL, NULL, '该电商企业高效完成了从数据剖析到建立用户画像，支持算法模型建立，最终找到精准营销目标。华矩科技协助该客户实现科学的方法管理数据与优化，促进数据价值的挖掘与运用，实现建立用户统一视图与提高营销的精准度，提高整体业务的运营能力与效率。', NULL, NULL, NULL, 'uploads/article/20180906/5b9116f30f2dc1.04335462.jpg', NULL, '2018-09-06 19:24:16', '2018-09-06 20:00:51', 0, 502, 0, NULL, '2018-09-06 19:23:10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
INSERT INTO `hl_article` VALUES (1433, '促进业务增长', NULL, NULL, NULL, '利用来自各数据源的标签构建完整的用户画像，快速识别分析每个客户的直接及渠道购买行为，筛选匹配消费需求意愿更吻合的目标用户群体精准投放，提高业务的精准获客率与转化率。', NULL, NULL, NULL, 'uploads/article/20180906/5b9116362ec2d8.60232810.png', NULL, '2018-09-06 19:22:28', '2018-09-06 19:57:42', 0, 501, 0, NULL, '2018-09-06 19:21:01', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
INSERT INTO `hl_article` VALUES (1435, '华矩科技助力某互联网保险企业布局以“数据驱动业务”的精准营销', NULL, NULL, NULL, '华矩科技协助该互联网保险企业通过数据营销技术的演进，为该互联网保险企业建立精准用户画像、用户匹配及互联网保险业业务场景开发，助力该互联网保险企业实现精准获客、增强用户黏性，并建立起保险消费者与保险企业的良性循环，降低获客成本及提高营销转化率。', NULL, NULL, NULL, 'uploads/article/20180906/5b9116b36fed10.79809051.jpg', NULL, '2018-09-06 19:24:52', '2018-09-06 19:59:47', 0, 502, 0, NULL, '2018-09-06 19:24:28', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
INSERT INTO `hl_article` VALUES (1436, '华矩科技助力某商业银行布局以“数据驱动”的大数据精准营销之路', NULL, NULL, NULL, '华矩科技针对该银行产品设计与人群定位，通过专业的数据处理技术进行深入挖掘分析，为产品的目标群体建立标签化数据，构建完整的多维用户画像，并基于各个渠道消费用户的特点、喜好、需求等多方位需求建立营销结合点，以个性化、场景化的精准推送，以恰如其分的联合营销来满足处于不同阶段和不同场景下的用户需求，从而实现精准获客、增强用户黏性，并在提高用户体验的同时，降低获客成本及提高营销转化率。', NULL, NULL, NULL, 'uploads/article/20180906/5b911660d0c509.06713272.jpg', NULL, '2018-09-06 19:25:24', '2018-09-06 19:58:24', 0, 502, 0, NULL, '2018-09-06 19:24:59', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
INSERT INTO `hl_article` VALUES (1437, 'TDWI清单报告：单一客户视图和全渠道营销的新数据实践', NULL, NULL, NULL, '营销是迄今为止在现代数字企业中最成功的业务功能之一。大部分成功来自数据管理，软件自动化和客户分析方面的重大进步，其规模空前，可实现客户的单一视图。成功还来自全渠道营销中的新的复杂实践，它利用单一客户视图和相关技术实践以整体和协调的方式向客户和潜在客户推销。\r\n该清单报告深入研究了现代数字营销的数据要求，重点关注单一客户视图和全渠道营销。该报告的目标是加快用户对不断发展的数据和营销最佳实践和工具的理解，以便用户组织更好地启动或扩展全渠道营销计划。', NULL, NULL, NULL, NULL, NULL, '2018-09-06 19:27:06', '2018-09-06 19:27:06', 0, 503, 0, NULL, '2018-09-06 19:26:18', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
INSERT INTO `hl_article` VALUES (1438, '华矩科技数据精准营销解决方案概述', NULL, NULL, NULL, '针对企业不同数据营销需求，华矩科技都可以提供有针对性的数据精准营销解决方案，助力企业借助外部资源，盘活内部数据，并基于数据共享实现跨界场景精准营销，在日益严苛的商业环境下实现业务增长，帮助中小企业共同获得大数据时代的红利。', NULL, NULL, NULL, NULL, NULL, '2018-09-06 19:27:47', '2018-09-06 19:27:47', 0, 503, 0, NULL, '2018-09-06 19:27:27', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 50, NULL, NULL);
