INSERT INTO `hl_article` VALUES (1474, '2018第二届数据质量管理国际峰会', NULL, NULL, '2018第二届数据质量管理国际峰会（DQMIS2018）将于2018年9月7日在北京希尔顿酒店隆重召开。峰会旨在开创中国数据质量管理研讨先河，解决影响数据分析和利用效能的“最后一公里”问题，聚焦大数据时代数据质量的新问题和新发展，邀请国内外各界专家从产学研企全方位多角度展开讨论，助力大数据产业环境的优化，推动中国大数据行业发展。', NULL, NULL, NULL, NULL, 'uploads/article/20180913/5b9a22b5710a58.61387490.png', NULL, '2018-09-13 16:41:25', '2018-09-21 13:18:26', 2, 433, 1, NULL, '2018-09-13 16:41:09', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 3450, NULL, NULL);
