INSERT INTO `hl_article_category` VALUES (460, 459, '华矩简介', NULL, '<div class=\"anitem\">\r\n	<div class=\"txt clearfix\">\r\n		<div class=\"td clearfix\">\r\n			<div class=\"pic\">\r\n				<img src=\"/resources/home/images/map.png\" /> \r\n			</div>\r\n			<div class=\"text clearfix\">\r\n				<p class=\"MsoNormal\">\r\n					<span style=\"font-family:宋体;\">华矩科技成立于2003年，总部位于广州，专注于企业级大数据技术与应用，是国内领先的数据治理技术与服务提供商。截止目前，华矩已在广州、北京、上海、深圳、香港设立交付中心，业务辐射大陆及港澳地区。<br />\r\n<br />\r\n业务涉及数据技术产品、数据治理服务、数据营运服务、大数据创新研发、大数据学院以及基础架构运维等六大部分，并在数据治理技术服务包括数据质量管理、元数据管理、数据可视化分析等，以及大数据营运服务包括数据交换与共享、精准营销等两大领域建立了核心竞争力。所服务的客户大部分是世界500强企业，涵盖制造、保险、电力、医疗、政府、交通等主要行业。<br />\r\n<br />\r\n愿景目标：成为国内领先的数据治理技术与服务提供商<br />\r\n“科技为先、质量为上、诚信为本、满意为标”是公司奉行的原则<br />\r\n“以创新为动力，视质量为生命，坦诚待人、务实发展”是公司一贯奉行的文化理念<br />\r\n公司以“客户满意最大化、员工成就最大化”为经营宗旨，以“以人才为依托，以市场为导向，以技术求发展为”经营理念，通过兼容“家”文化与“狼”文化打造高素质的团队，从而推动建立起让客户可信赖的企业形象。<br />\r\n</span>\r\n				</p>\r\n			</div>\r\n		</div>\r\n		<p>\r\n			<br />\r\n		</p>\r\n	</div>\r\n</div>', NULL, NULL, NULL, NULL, NULL, NULL, 'index2-gaishu', 71, '2018-08-05 23:56:01', '2018-09-20 17:42:30', NULL, NULL, NULL, NULL, NULL, NULL, '', NULL);
