INSERT INTO `hl_article_category` VALUES (474, 469, '大数据实习基地', NULL, NULL, '这个设置，挣的是反人类华矩科技，作为一家国内领先的数据治理及服务商，一直致力于中外技术交流以及为中国引进海外高科技技术及产品。企业的发展离不开优秀人才，华矩在注重国际新技术产品引进同时，积极与高校展开合作，分别在广州、深圳、北京全面开展实习生计划，在信息技术、大数据以及外语等领域为高校学生提供社会实践机会。', NULL, NULL, NULL, NULL, NULL, 'index2-shixijidi', 84, '2018-08-06 01:49:51', '2018-09-20 17:42:30', NULL, NULL, NULL, NULL, NULL, NULL, '', NULL);
